import React, { useState } from 'react';

const TouristPlaces = () => {
  const [activeTab, setActiveTab] = useState('uttarakhand');

  return (
    <div style={{ marginTop: '70px', padding: '4rem 0' }}>
      <div className="container">
        <h1 style={{ textAlign: 'center', fontSize: '3rem', marginBottom: '2rem' }}>Tourist Places</h1>
        
        <div className="tabs">
          <button 
            className={`tab ${activeTab === 'uttarakhand' ? 'active' : ''}`}
            onClick={() => setActiveTab('uttarakhand')}
          >
            Uttarakhand
          </button>
          <button 
            className={`tab ${activeTab === 'himachal' ? 'active' : ''}`}
            onClick={() => setActiveTab('himachal')}
          >
            Himachal Pradesh
          </button>
          <button 
            className={`tab ${activeTab === 'nepal' ? 'active' : ''}`}
            onClick={() => setActiveTab('nepal')}
          >
            Nepal
          </button>
        </div>

        <div className="tab-content active" style={{ background: 'white', padding: '2rem', borderRadius: '15px', marginTop: '2rem' }}>
          <h2>üèîÔ∏è Places to Visit in {activeTab}</h2>
          <p>Explore breathtaking destinations, hidden gems, and must-visit attractions in the {activeTab} region.</p>
        </div>
      </div>
    </div>
  );
};

export default TouristPlaces;
