import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import Logo from '../Logo/Logo';
import './Header.css';

const Header = () => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  return (
    <header className="header">
      <div className="container">
        <div className="header-content">
          <Link to="/" className="logo-link">
            <Logo size="medium" />
          </Link>
          
          <nav className={`nav ${isMenuOpen ? 'nav-open' : ''}`}>
            <Link to="/" className="nav-link">Home</Link>
            <Link to="/hotels" className="nav-link">Hotels</Link>
            <Link to="/history" className="nav-link">History</Link>
            <Link to="/geography" className="nav-link">Geography</Link>
            <Link to="/events" className="nav-link">Events</Link>
            <Link to="/tourist-places" className="nav-link">Tourist Places</Link>
            <Link to="/radio" className="nav-link">Radio</Link>
            <Link to="/shaadi" className="nav-link">Shaadi</Link>
            <Link to="/festivals" className="nav-link">Festivals</Link>
            <Link to="/about" className="nav-link">About</Link>
          </nav>
          
          <button 
            className="menu-toggle"
            onClick={() => setIsMenuOpen(!isMenuOpen)}
          >
            <span></span>
            <span></span>
            <span></span>
          </button>
        </div>
      </div>
    </header>
  );
};

export default Header;
