import React, { useState } from 'react';

const History = () => {
  const [activeTab, setActiveTab] = useState('uttarakhand');

  return (
    <div style={{ marginTop: '70px', padding: '4rem 0' }}>
      <div className="container">
        <h1 style={{ textAlign: 'center', fontSize: '3rem', marginBottom: '2rem' }}>Rich Tribal History</h1>
        
        <div className="tabs">
          <button 
            className={`tab ${activeTab === 'uttarakhand' ? 'active' : ''}`}
            onClick={() => setActiveTab('uttarakhand')}
          >
            Uttarakhand
          </button>
          <button 
            className={`tab ${activeTab === 'himachal' ? 'active' : ''}`}
            onClick={() => setActiveTab('himachal')}
          >
            Himachal Pradesh
          </button>
          <button 
            className={`tab ${activeTab === 'nepal' ? 'active' : ''}`}
            onClick={() => setActiveTab('nepal')}
          >
            Nepal
          </button>
        </div>

        <div className="tab-content active" style={{ background: 'white', padding: '2rem', borderRadius: '15px', marginTop: '2rem' }}>
          <h2>Tribal Heritage of {activeTab}</h2>
          <p>Discover the ancient cultures and traditions of the indigenous peoples who have called these mountains home for thousands of years.</p>
          
          {activeTab === 'uttarakhand' && (
            <div>
              <h3>üèîÔ∏è Garhwali & Kumaoni Peoples</h3>
              <p>The indigenous communities of Uttarakhand with their rich cultural heritage, traditional practices, and deep connection to the Himalayas.</p>
            </div>
          )}
          
          {activeTab === 'himachal' && (
            <div>
              <h3>üèîÔ∏è Kinnaura & Gaddi Communities</h3>
              <p>The mountain peoples of Himachal Pradesh known for their unique customs, seasonal migrations, and adaptation to high-altitude living.</p>
            </div>
          )}
          
          {activeTab === 'nepal' && (
            <div>
              <h3>üèîÔ∏è Sherpa & Mountain Communities</h3>
              <p>The legendary mountain peoples of Nepal, including the Sherpa, Tamang, and Gurung communities, renowned for their mountaineering skills and Buddhist culture.</p>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default History;
