import React from 'react';
import { useTranslation } from '../../hooks/useTranslation';

const About = () => {
  const { t } = useTranslation();
  return (
    <div style={{ marginTop: '70px', padding: '4rem 0' }}>
      <div className="container">
        <h1 style={{ textAlign: 'center', fontSize: '3rem', marginBottom: '2rem' }}>рдкрд╣рд╛рдбрд╝реА рд╡рд┐рд░рд╛рд╕рдд рдХреЗ рдмрд╛рд░реЗ рдореЗрдВ</h1>
        <div style={{ padding: '2rem', background: 'white', borderRadius: '15px' }}>
          <h2>ЁЯПФя╕П рд╣рдорд╛рд░рд╛ рдорд┐рд╢рди</h2>
          <p>рдкрд╣рд╛рдбрд╝реА рд╡рд┐рд░рд╛рд╕рдд рдкрд╣рд╛рдбрд╝реА рдХреНрд╖реЗрддреНрд░реЛрдВ - рдЙрддреНрддрд░рд╛рдЦрдВрдб, рд╣рд┐рдорд╛рдЪрд▓ рдкреНрд░рджреЗрд╢ рдФрд░ рдиреЗрдкрд╛рд▓ рдХреА рд╕рдореГрджреНрдз рд╕рд╛рдВрд╕реНрдХреГрддрд┐рдХ рдкрд░рдВрдкрд░рд╛рдУрдВ, рдЗрддрд┐рд╣рд╛рд╕ рдФрд░ рдкреНрд░рд╛рдХреГрддрд┐рдХ рд╕реБрдВрджрд░рддрд╛ рдХреЛ рд╕рдВрд░рдХреНрд╖рд┐рдд рдФрд░ рдмрдврд╝рд╛рд╡рд╛ рджреЗрдиреЗ рдХреЗ рд▓рд┐рдП рд╕рдорд░реНрдкрд┐рдд рд╣реИред</p>
          
          <h3>ЁЯМЯ What We Do</h3>
          <ul>
            <li>ЁЯУЪ Document and share tribal history and cultural heritage</li>
            <li>ЁЯПи Connect travelers with authentic mountain accommodations</li>
            <li>ЁЯТЭ Help Pahadi communities find life partners within their culture</li>
            <li>ЁЯОЙ Promote traditional festivals and cultural events</li>
            <li>ЁЯУ╗ Preserve folk music and oral traditions</li>
            <li>ЁЯЧ║я╕П Guide visitors to explore geographical and cultural sites</li>
          </ul>
          
          <h3>ЁЯОп Our Vision</h3>
          <p>To create a comprehensive platform that celebrates the diversity of Pahadi cultures while supporting sustainable tourism and community connections in the mountain regions of India and Nepal.</p>
          
          <h3>ЁЯдЭ Join Our Mission</h3>
          <p>Whether you're a traveler seeking authentic mountain experiences, a community member wanting to share your heritage, or someone looking to connect with Pahadi culture, we welcome you to be part of our growing community.</p>
        </div>
      </div>
    </div>
  );
};

export default About;
